CREATE TABLE IF NOT EXISTS `#__formmaker` (  `id` int(11) NOT NULL auto_increment,  `title` varchar(127) NOT NULL,  `mail` varchar(128) NOT NULL,  `form` longtext NOT NULL,  `form_front` longtext NOT NULL,  `theme` int(11) NOT NULL,  `javascript` text NOT NULL,  `submit_text` longtext NOT NULL,  `url` varchar(200) NOT NULL,  `submit_text_type` tinyint(4) NOT NULL,  `script_mail` text NOT NULL,  `script_mail_user` text NOT NULL,  `counter` int(11) NOT NULL,  `published` int(11) NOT NULL,  `label_order` text NOT NULL,  `label_order_current` text NOT NULL,  `article_id` int(11) NOT NULL,  `pagination` varchar(128) NOT NULL,  `show_title` varchar(128) NOT NULL,  `show_numbers` varchar(128) NOT NULL,  `public_key` varchar(50) NOT NULL,  `private_key` varchar(50) NOT NULL,  `recaptcha_theme` varchar(20) NOT NULL,  `paypal_mode` int(2) NOT NULL,  `checkout_mode` varchar(20) NOT NULL,  `paypal_email` varchar(50) NOT NULL,  `payment_currency` varchar(20) NOT NULL,  `tax` int(11) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=11 ;CREATE TABLE IF NOT EXISTS `#__formmaker_submits` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `form_id` int(11) NOT NULL,  `element_label` varchar(128) NOT NULL,  `element_value` varchar(600) NOT NULL,  `group_id` int(11) NOT NULL,  `date` datetime NOT NULL,  `ip` varchar(32) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=232 ;CREATE TABLE IF NOT EXISTS `#__formmaker_themes` (  `id` int(11) NOT NULL AUTO_INCREMENT,  `title` varchar(200) NOT NULL,  `css` text NOT NULL,  `default` tinyint(4) NOT NULL,  PRIMARY KEY (`id`)) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=42 ;CREATE TABLE IF NOT EXISTS `#__formmaker_views` (  `form_id` int(11) NOT NULL,  `views` int(50) NOT NULL,  PRIMARY KEY (`form_id`)) ENGINE=MyISAM DEFAULT CHARSET=utf8;CREATE TABLE IF NOT EXISTS `#__formmaker_sessions` (
`id` int(11) unsigned NOT NULL AUTO_INCREMENT,  `form_id` int(20) NOT NULL,  `group_id` int(20) NOT NULL,  `ip` varchar(20) NOT NULL,  `ord_date` datetime NOT NULL,  `ord_last_modified` datetime NOT NULL,  `status` varchar(50) NOT NULL,  `full_name` varchar(100) NOT NULL,  `email` varchar(50) NOT NULL,  `phone` varchar(50) NOT NULL,  `mobile_phone` varchar(255) NOT NULL,  `fax` varchar(255) NOT NULL,  `address` varchar(300) NOT NULL,  `paypal_info` text NOT NULL,  `without_paypal_info` text NOT NULL,  `ipn` varchar(20) NOT NULL,  `checkout_method` varchar(20) NOT NULL,  `tax` float NOT NULL,  `shipping` float NOT NULL,  `shipping_type` varchar(200) NOT NULL,  `read` int(11) NOT NULL,  `total` float NOT NULL,  `currency` varchar(24) NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;

